mapper.select:
select username, truename, phone, email, department
from salarysystem.staff
where username = #{username}

select username, truename, phone, email, department
from salarysystem.staff
where username = #{username}
  and password = #{password}

mapper.update:
update salarysystem.staff


        password = #{password},


        truename = #{truename},


        phone = #{phone},


        email = #{email}


where username = #{username}
